import React from "react";
import { IconBrandAdobe, IconBrandHtml5 } from "@tabler/icons-react";

function About() {
  const frontEndSkills = [
    "HTML5",
    "CSS3",
    "JavaScript",
    "React.js",
    "Node.js",
    "Bootstrap",
    "Tailwind",
    "API Integration",
    "Responsive Web Design",
    "Web Performance Optimization",
  ];

  const uiUxSkills = [
    "Adobe Creative Suite (Photoshop, Illustrator, XD)",
    "Figma",
    "User Research and Prototyping",
    "Wireframing and Mockups",
  ];

  const additionalSkills = [
    "Express.js",
    "MySQL",
    "Firebase",
    "Visual Design Principles",
    "Version Control (Git)",
    "Cross-Browser Compatibility",
    "Web Accessibility (WCAG)",
    "SEO Best Practices",
    "Time Management",
    "Problem Solving",
    "Teamwork",
    "Communication",
    "Fast Learner",
  ];

  const skillIcons = {
    HTML5: <IconBrandHtml5 size={24} strokeWidth={2} />,
    CSS3: <IconBrandCss3 size={24} strokeWidth={2} />,
    JavaScript: <IconBrandJavascript size={24} strokeWidth={2} />,
    "React.js": <IconBrandReact size={24} strokeWidth={2} />,
    "Node.js": <IconBrandNodejs size={24} strokeWidth={2} />,
    Bootstrap: <IconBrandBootstrap size={24} strokeWidth={2} />,
    Tailwind: <IconBrandTailwind size={24} strokeWidth={2} />,
    "API Integration": <IconBrandApi size={24} strokeWidth={2} />,
    "Responsive Web Design": <IconBrandFigma size={24} strokeWidth={2} />,
    "Web Performance Optimization": (
      <IconBrandFramer size={24} strokeWidth={2} />
    ),
    "Adobe Creative Suite (Photoshop, Illustrator, XD)": (
      <IconBrandAdobe size={24} strokeWidth={2} />
    ),
    Figma: <IconBrandFigma size={24} strokeWidth={2} />,
  };

  const renderSkillsWithIcons = (skills) => {
    return skills.map((skill, index) => (
      <li key={index}>
        {skillIcons[skill] && <span className="icon">{skillIcons[skill]}</span>}{" "}
        {skill}
      </li>
    ));
  };

  return (
    <>
      <div className="about-tech-stack">
        <h2>My Tech Stack</h2>
        <div>
          <h3>Front-End Development</h3>
          <ul>{renderSkillsWithIcons(frontEndSkills)}</ul>
        </div>
        <div>
          <h3>UI/UX Design Tools</h3>
          <ul>{renderSkillsWithIcons(uiUxSkills)}</ul>
        </div>
        <div>
          <h3>Additional Skills</h3>
          <ul>{renderSkillsWithIcons(additionalSkills)}</ul>
        </div>
        <p>
          I specialize in creating intuitive user interfaces and seamless user
          experiences, with a focus on responsive design and modern web
          technologies.
        </p>
      </div>
    </>
  );
}

export default About;
