import React, { useState } from "react";
import { ReactComponent as Portuguese } from "../../assets/images/Portuguese.svg";
import { ReactComponent as English } from "../../assets/images/English.svg";

function LanguageToggle() {
  const [language, setLanguage] = useState("pt"); // Padrão para Português

  const toggleLanguage = () => {
    if (language === "pt") {
      setLanguage("en");
    } else {
      setLanguage("pt");
    }
  };

  return (
    <div className="language-toggle">
      <button onClick={toggleLanguage}>
        {language === "pt" ? <Portuguese /> : <English />}
      </button>
    </div>
  );
}

export default LanguageToggle;
