import React from "react";
import { Link, useLocation } from "react-router-dom";
import "./NavBar.styles.css";
import { IconFolder, IconFolderOpen } from "@tabler/icons-react";

function Navbar() {
  const location = useLocation();

  const getPageNameAndIcon = () => {
    let pageName = "";
    let icon = <IconFolder className="icon" />;

    switch (location.pathname) {
      case "/":
        pageName = "Home";
        icon = <IconFolderOpen className="icon" />;
        break;
      case "/projects":
        pageName = "Projects";
        icon =
          location.pathname === "/projects" ? (
            <IconFolderOpen className="icon" />
          ) : (
            <IconFolder className="icon" />
          );
        break;
      case "/contact":
        pageName = "Contact";
        icon =
          location.pathname === "/contact" ? (
            <IconFolderOpen className="icon" />
          ) : (
            <IconFolder className="icon" />
          );
        break;
      default:
        break;
    }

    return { pageName, icon };
  };

  const { pageName, icon } = getPageNameAndIcon();

  return (
    <div className="navbar">
      <ul>
        <li>
          <Link to="/">{icon}</Link>
        </li>
        <li>
          <Link to="/projects">{icon}</Link>
        </li>
        <li>
          <Link to="/contact">{icon}</Link>
        </li>
      </ul>

      <div className="page-name">
        <h3>{pageName}</h3>
      </div>
    </div>
  );
}

export default Navbar;
