import React from "react";
import { CSSTransition } from "react-transition-group";
import "./About.styles.css";

function About() {
  const [inProp, setInProp] = React.useState(false);

  React.useEffect(() => {
    setInProp(true);
  }, []);

  return (
    <>
      <CSSTransition in={inProp} timeout={300} classNames="fade" unmountOnExit>
        <div className="home main-content">
          <div className="hero">
            <div className="hero-text">
              <div className="hero-title">
                <p>Hey, I'm Fernando!</p>
              </div>
              <p>
                I'm a <span>Front-End Developer</span> and
                <span> UI/UX Designer</span> who loves building fun websites,
                apps and other digital stuff.
              </p>
              <p>
                Anyway, have a look around and get in touch if you'd like to
                work together!
              </p>
            </div>
          </div>
        </div>
      </CSSTransition>
    </>
  );
}

export default About;
