import React from "react";
import { CSSTransition } from "react-transition-group";
import "./Home.styles.css";
import {
  IconBrandGithub,
  IconBrandLinkedin,
  IconFolder,
  IconMail,
  IconUser,
} from "@tabler/icons-react";
import { Link } from "react-router-dom";

function Home() {
  const [inProp, setInProp] = React.useState(false);

  React.useEffect(() => {
    setInProp(true);
  }, []);

  return (
    <>
<motion.div 
      initial="out" 
      animate="in"
      exit="out"
      variants={{
        in: { 
          opacity: 1
        },
        out: {
          opacity: 0
        }
      }}
      transition={{ duration: 0.5 }}
    >
        <div className="home main-content">
          <div className="hero">
            <div className="hero-text">
              <div className="hero-title">
                <p>Hey, I'm Fernando!</p>
              </div>
              <p>
                I'm a <span>Front-End Developer</span> and
                <span> UI/UX Designer</span> who loves building fun websites,
                apps and other digital stuff.
              </p>
              <p>
                Anyway, have a look around and get in touch if you'd like to
                work together!
              </p>
              <div className="hero-social">
                <a
                  href="https://github.com/nandodani"
                  target="_blank"
                  rel="noreferrer"
                >
                  <IconBrandGithub />
                </a>
                <a
                  href="http://www.linkedin.com/in/fernando-apostolo"
                  target="_blank"
                  rel="noreferrer"
                >
                  <IconBrandLinkedin />
                </a>
              </div>
            </div>
          </div>
          <div className="links">
            <Link to="/about">
              <IconUser />
              <span>Find out more about me!</span>
            </Link>
            <Link to="/projects">
              <IconFolder />
              <span>Find out more about my projects!</span>
            </Link>
            <Link to="/contact">
              <IconMail />
              <span>Reach out to me!</span>
            </Link>
          </div>
        </div>
      </motion.div>
    </>
  );
}

export default Home;
