import React from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Outlet,
  useNavigate,
} from "react-router-dom";
import { TransitionGroup, CSSTransition } from "react-transitions-group";
import "./App.css";
import Home from "./components/Home/Home";
import Projects from "./components/Projects/Projects";
import Contact from "./components/Contact/Contact";
import Navbar from "./components/NavBar/NavBar";

function App() {
  return (
    <div className="App">
      <div className="App-bg-effect">
        <Router>
          <div className="App-main">
            <header className="App-header">
              <Navbar />
            </header>
            <TransitionGroup> {/* Wrap the entire Routes element */}
              <Routes>
                <CSSTransition
                  key={window.location.pathname} // Use the pathname as the key
                  classNames="fade"
                  timeout={300}
                >
                  <Route path="/" element={<Home />} />
                </CSSTransition>
                <CSSTransition
                  key={window.location.pathname}
                  classNames="fade"
                  timeout={300}
                >
                  <Route path="/projects" element={<Projects />} />
                </CSSTransition>
                <CSSTransition
                  key={window.location.pathname}
                  classNames="fade"
                  timeout={300}
                >
                  <Route path="/contact" element={<Contact />} />
                </CSSTransition>
              </Routes>
            </TransitionGroup>
          </div>
        </Router>
      </div>
    </div>
  );
}

export default App;
