import React from "react";
import { CSSTransition } from "react-transition-group";
import "./Home.styles.css";
import { IconBrandGithub, IconBrandLinkedin } from "@tabler/icons-react";

function Home() {
  const [inProp, setInProp] = React.useState(false);

  React.useEffect(() => {
    setInProp(true);
  }, []);

  const [hovered, setHovered] = useState(null);

  const handleMouseEnter = (event) => {
    setHovered(event.target.getAttribute("data-id"));
  };

  const handleMouseLeave = () => {
    setHovered(null);
  };

  return (
    <>
      <CSSTransition in={inProp} timeout={300} classNames="fade" unmountOnExit>
        <div className="home main-content">
          <div className="hero">
            <div className="hero-text">
              <div className="hero-title">
                <p>Hey, I'm Fernando!</p>
                <p> </p>
              </div>
              <p>
                I'm a <span>Front-End Developer</span> and
                <span> UI/UX Designer</span> who loves building fun websites,
                apps and other digital stuff.
              </p>
              <p>
                Anyway, have a look around and get in touch if you'd like to
                work together!
              </p>
              <div className="hero-social">
                <a
                  href="https://github.com/nandodani"
                  target="_blank"
                  rel="noreferrer"
                >
                  <IconBrandGithub />
                </a>
                <a
                  href="http://www.linkedin.com/in/fernando-apostolo"
                  target="_blank"
                  rel="noreferrer"
                >
                  <IconBrandLinkedin />
                </a>
              </div>
            </div>
          </div>
          <div className="links">
        <a
          href="/about"
          data-id="about"
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        >
          About
        </a>
        <a
          href="/projects"
          data-id="projects"
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        >
          Projects
        </a>
        <a
          href="/contact"
          data-id="contact"
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        >
          Contacts
        </a>
      </div>

      <CSSTransition
        in={hovered !== null}
        timeout={300}
        classNames="tooltip"
        unmountOnExit
      >
        {(state) => (
          <div className={`tooltip-${state}`}>
            {hovered === "about" && <span>I want to know you better</span>}
            {hovered === "projects" && <span>Check out my projects</span>}
            {hovered === "contact" && <span>Get in touch with me</span>}
          </div>
        )}
      </CSSTransition>
        </div>
      </CSSTransition>
    </>
  );
}

export default Home;
