import React from "react";
import { Link, useLocation } from "react-router-dom";
import { CSSTransition } from "react-transition-group";
import "./NavBar.styles.css";
import {
  IconFolder,
  IconFolderOpen,
  IconHome,
  IconHomeHeart,
  IconMail,
  IconMailOpened,
} from "@tabler/icons-react";

function Navbar() {
  const location = useLocation();
  const getPageName = () => {
    switch (location.pathname) {
      case "/":
        return "Home";
      case "/projects":
        return "Projects";
      case "/contact":
        return "Contact";
      default:
        return "";
    }
  };

  const linkStyles = {
    color: "#a6a6a6",
  };

  return (
    <div className="navbar">
      <ul>
        <li>
          <Link to="/" style={linkStyles}>
            {location.pathname === "/" ? (
              <IconHomeHeart className="icon" />
            ) : (
              <IconHome className="icon" />
            )}
          </Link>
        </li>
        <li>
          <Link to="/projects" style={linkStyles}>
            {location.pathname === "/projects" ? (
              <IconFolderOpen className="icon" />
            ) : (
              <IconFolder className="icon" />
            )}
          </Link>
        </li>
        <li>
          <Link to="/contact" style={linkStyles}>
            {location.pathname === "/contact" ? (
              <IconMailOpened className="icon" />
            ) : (
              <IconMail className="icon" />
            )}
          </Link>
        </li>
      </ul>

      <CSSTransition
        in={true}
        timeout={500} // Define a duração da animação em milissegundos
        classNames="slide"
        unmountOnExit
      >
        <div className="page-name">
          <h3>{getPageName()}</h3>
        </div>
      </CSSTransition>
    </div>
  );
}

export default Navbar;
