import React from "react";
import { CSSTransition } from "react-transition-group";
import "./Home.styles.css";
import { IconBrandGithub, IconBrandLinkedin } from "@tabler/icons-react";

function Home() {
  const [inProp, setInProp] = React.useState(false);

  React.useEffect(() => {
    setInProp(true);
  }, []);

  return (
    <div className="home main-content">
      <CSSTransition in={inProp} timeout={300} classNames="fade" unmountOnExit>
        <div className="hero">
          <div className="hero-text">
            <h1>Hey, I'm Fernando! üëãüèª</h1>
            <p>
              I'm a <span>Front-End Developer</span> and
              <span> UI/UX Designer</span> who loves building fun websites, apps
              and other digital stuff.
            </p>
            <p>
              Anyway, have a look around and get in touch if you'd like to work
              together!
            </p>
          </div>
          <div className="hero-social">
            <a
              href="https://github.com/nandodani"
              target="_blank"
              rel="noreferrer"
            >
              <IconBrandGithub />
            </a>
            <a
              href="http://www.linkedin.com/in/fernando-apostolo"
              target="_blank"
              rel="noreferrer"
            >
              <IconBrandLinkedin />
            </a>
          </div>
        </div>
      </CSSTransition>
    </div>
  );
}

export default Home;
