import React from "react";
import { Link, useLocation } from "react-router-dom";
import "./NavBar.styles.css";
import { IconFolder, IconFolderOpen } from "@tabler/icons-react";

function Navbar() {
  const location = useLocation();

  const getIcon = (path) => {
    return location.pathname === path ? <IconFolderOpen className="icon" /> : <IconFolder className="icon" />;
  };

  return (
    <div className="navbar">
      <ul>
        <li>
          <Link to="/">
            {getIcon("/")}
          </Link>
        </li>
        <li> 
          <Link to="/projects">
            {getIcon("/projects")}
          </Link>
        </li>
        <li>
          <Link to="/contact">
            {getIcon("/contact")}
          </Link>
        </li>
      </ul>

      <div className="page-name">
        <h3>{getPageName()}</h3>
      </div>
    </div>
  );
}

export default Navbar;
